// data transformation
var years = []; // xAxis = year
var months = ['1月', '2月', '3月', '4月', '5月', '6月', '7月', '8月', '9月', '10月', '11月', '12月'] // yAxis = month
var months2 = ['01', '02', '03', '04', '05', '06', '07', '08', '09', '10', '11', '12'] // to YYYY-MM-DD
var matrix = []; // input data format: [ xIndex, yIndex, Value]
var maxValue = 0
for (var i = 0; i < res.length; i++) {
    years.push(res[i][0])
    for (var j = 1; j <= 12; j++) {
        var v = res[i][j]
        if (v > maxValue) {
            maxValue = v
        }
        matrix.push([i, j - 1, res[i][j]]);
    }
}
// up round max threshold to 100
maxValue = 100 * Math.ceil(maxValue / 100);

var station = res[0][13]

